import React, { useMemo, useRef } from 'react';
import { View, ScrollView, Text, ViewStyle, TextStyle, Platform } from 'react-native';
import * as Diff from 'diff';
import { useColorScheme } from '@/components/useColorScheme';

// Types for diff changes
interface DiffChange {
    type: 'add' | 'remove' | 'normal';
    value: string;
    count?: number;
    added?: boolean;
    removed?: boolean;
}

interface DiffLine {
    type: 'add' | 'remove' | 'normal';
    content: string;
    oldLineNumber?: number;
    newLineNumber?: number;
}

interface DiffHunk {
    oldStart: number;
    oldLines: number;
    newStart: number;
    newLines: number;
    lines: DiffLine[];
}

interface DiffViewProps {
    oldText: string;
    newText: string;
    mode?: 'unified' | 'split';
    diffType?: 'chars' | 'words' | 'lines' | 'sentences';
    contextLines?: number;
    showLineNumbers?: boolean;
    showDiffStats?: boolean;
    oldTitle?: string;
    newTitle?: string;
    className?: string;
    style?: ViewStyle;
    maxHeight?: number;
    wrapLines?: boolean;
    fontScaleX?: number;
}

// Define colors inline - using traditional diff colors
const COLORS = {
    light: {
        surface: '#FFFFFF',
        surfaceVariant: '#F8F8F8',
        onSurface: '#000000',
        onSurfaceVariant: '#666666',
        outline: '#E0E0E0',
        success: '#28A745',
        error: '#DC3545',
        // Traditional diff colors
        addedBg: '#E6FFED',
        addedBorder: '#34D058',
        addedText: '#24292E',
        removedBg: '#FFEEF0',
        removedBorder: '#D73A49',
        removedText: '#24292E',
        contextBg: '#F6F8FA',
        contextText: '#586069',
        lineNumberBg: '#F6F8FA',
        lineNumberText: '#959DA5',
        hunkHeaderBg: '#F1F8FF',
        hunkHeaderText: '#005CC5',
        leadingSpaceDot: '#E8E8E8',
    },
    dark: {
        surface: '#0D1117',
        surfaceVariant: '#161B22',
        onSurface: '#C9D1D9',
        onSurfaceVariant: '#8B949E',
        outline: '#30363D',
        success: '#3FB950',
        error: '#F85149',
        // Traditional diff colors for dark mode
        addedBg: '#0D2E1F',
        addedBorder: '#3FB950',
        addedText: '#C9D1D9',
        removedBg: '#3F1B23',
        removedBorder: '#F85149',
        removedText: '#C9D1D9',
        contextBg: '#161B22',
        contextText: '#8B949E',
        lineNumberBg: '#161B22',
        lineNumberText: '#6E7681',
        hunkHeaderBg: '#161B22',
        hunkHeaderText: '#58A6FF',
        leadingSpaceDot: '#2A2A2A',
    }
};

export const DiffView: React.FC<DiffViewProps> = ({
    oldText,
    newText,
    mode = 'unified',
    diffType = 'lines',
    contextLines = 3,
    showLineNumbers = true,
    showDiffStats = true,
    oldTitle = 'Original',
    newTitle = 'Modified',
    className = '',
    style,
    maxHeight = 500,
    wrapLines = true,
    fontScaleX = 1,
}) => {
    const colorScheme = useColorScheme();
    const colors = COLORS[colorScheme ?? 'light'];
    const unifiedScrollRef = useRef<ScrollView>(null);
    const splitScrollRef = useRef<ScrollView>(null);

    // Calculate diff based on type
    const diffResult = useMemo(() => {
        let changes: DiffChange[] = [];

        switch (diffType) {
            case 'chars':
                changes = Diff.diffChars(oldText, newText) as DiffChange[];
                break;
            case 'words':
                changes = Diff.diffWords(oldText, newText) as DiffChange[];
                break;
            case 'sentences':
                changes = Diff.diffSentences(oldText, newText) as DiffChange[];
                break;
            case 'lines':
            default:
                changes = Diff.diffLines(oldText, newText) as DiffChange[];
                break;
        }

        return changes;
    }, [oldText, newText, diffType]);

    // Convert diff changes to lines with line numbers
    const { lines, stats } = useMemo(() => {
        const allLines: DiffLine[] = [];
        let oldLineNum = 1;
        let newLineNum = 1;
        let additions = 0;
        let deletions = 0;

        diffResult.forEach((change) => {
            const changeLines = change.value.split('\n').filter((line, index, arr) =>
                // Remove empty last line from split
                !(index === arr.length - 1 && line === '')
            );

            changeLines.forEach((line) => {
                if (change.added) {
                    allLines.push({
                        type: 'add',
                        content: line,
                        newLineNumber: newLineNum++,
                    });
                    additions++;
                } else if (change.removed) {
                    allLines.push({
                        type: 'remove',
                        content: line,
                        oldLineNumber: oldLineNum++,
                    });
                    deletions++;
                } else {
                    allLines.push({
                        type: 'normal',
                        content: line,
                        oldLineNumber: oldLineNum++,
                        newLineNumber: newLineNum++,
                    });
                }
            });
        });

        return { lines: allLines, stats: { additions, deletions } };
    }, [diffResult]);

    // Create hunks with context
    const hunks = useMemo(() => {
        const result: DiffHunk[] = [];
        const changes = lines.map((line, index) => ({ ...line, index }))
            .filter(line => line.type !== 'normal');

        if (changes.length === 0) {
            // No changes, return single hunk with all lines if they exist
            if (lines.length > 0) {
                result.push({
                    oldStart: 1,
                    oldLines: lines.filter(l => l.oldLineNumber).length,
                    newStart: 1,
                    newLines: lines.filter(l => l.newLineNumber).length,
                    lines: lines,
                });
            }
            return result;
        }

        // Group changes into hunks with context
        let currentHunk: DiffLine[] = [];
        let lastIncludedIndex = -1;

        changes.forEach((change, i) => {
            const startContext = Math.max(0, change.index - contextLines);
            const endContext = Math.min(lines.length - 1, change.index + contextLines);

            // Add lines from last included index to current hunk
            for (let j = Math.max(lastIncludedIndex + 1, startContext); j <= endContext; j++) {
                currentHunk.push(lines[j]);
            }
            lastIncludedIndex = endContext;

            // Check if we should start a new hunk
            const nextChange = changes[i + 1];
            if (nextChange && nextChange.index - endContext > contextLines * 2) {
                // Finish current hunk
                if (currentHunk.length > 0) {
                    const firstLine = currentHunk[0];
                    result.push({
                        oldStart: firstLine.oldLineNumber || 1,
                        oldLines: currentHunk.filter(l => l.oldLineNumber).length,
                        newStart: firstLine.newLineNumber || 1,
                        newLines: currentHunk.filter(l => l.newLineNumber).length,
                        lines: currentHunk,
                    });
                }
                currentHunk = [];
            }
        });

        // Add remaining lines to last hunk
        if (currentHunk.length > 0) {
            const firstLine = currentHunk[0];
            result.push({
                oldStart: firstLine.oldLineNumber || 1,
                oldLines: currentHunk.filter(l => l.oldLineNumber).length,
                newStart: firstLine.newLineNumber || 1,
                newLines: currentHunk.filter(l => l.newLineNumber).length,
                lines: currentHunk,
            });
        }

        return result;
    }, [lines, contextLines]);

    // Styles
    const containerStyle: ViewStyle = {
        backgroundColor: colors.surface,
        borderWidth: 0,
        flex: 1,
        ...style,
    };

    const headerStyle: ViewStyle = {
        backgroundColor: colors.surfaceVariant,
        paddingHorizontal: 16,
        paddingVertical: 10,
        borderBottomWidth: 1,
        borderBottomColor: colors.outline,
        flexDirection: 'row',
        justifyContent: 'space-between',
        alignItems: 'center',
    };

    const titleStyle: TextStyle = {
        fontSize: 14,
        fontWeight: '600',
        color: colors.onSurface,
    };

    const statsStyle: TextStyle = {
        fontSize: 12,
        fontFamily: Platform.select({
            ios: 'Menlo',
            android: 'monospace',
            default: 'monospace'
        }),
    };

    // Calculate line number width based on max line count
    const maxLineNumber = Math.max(
        lines.filter(l => l.oldLineNumber).length,
        lines.filter(l => l.newLineNumber).length
    );
    const lineNumberWidth = Math.max(30, (maxLineNumber.toString().length * 8) + 12);

    const lineNumberStyle: TextStyle = {
        fontSize: 11,
        fontFamily: Platform.select({
            ios: 'Menlo',
            android: 'monospace',
            default: 'monospace'
        }),
        color: colors.lineNumberText,
        width: lineNumberWidth,
        textAlign: 'right',
        paddingRight: 8,
        backgroundColor: colors.lineNumberBg,
    };

    const lineContentStyle: TextStyle = {
        fontSize: 13,
        fontFamily: Platform.select({
            ios: 'Menlo',
            android: 'monospace',
            default: 'monospace'
        }),
        flex: 1,
    };

    // Helper function to format line content
    const formatLineContent = (content: string) => {
        // Just trim trailing spaces, we'll handle leading spaces in rendering
        return content.trimEnd();
    };

    // Helper function to render line with styled leading space dots
    const renderLineContent = (content: string, textStyle: any) => {
        const formatted = formatLineContent(content);
        const leadingSpaces = formatted.match(/^( +)/);

        if (leadingSpaces) {
            const dots = '\u00b7'.repeat(leadingSpaces[0].length);
            const restOfLine = formatted.slice(leadingSpaces[0].length);

            return (
                <View style={{ flexDirection: 'row', flex: 1 }}>
                    <Text style={[textStyle, { transform: [{ scaleX: fontScaleX }] }]}>
                        <Text style={{ color: colors.leadingSpaceDot }}>{dots}</Text>
                        {restOfLine}
                    </Text>
                </View>
            );
        }

        return (
            <View style={{ flexDirection: 'row', flex: 1 }}>
                <Text style={[textStyle, { transform: [{ scaleX: fontScaleX }] }]}>{formatted}</Text>
            </View>
        );
    };

    // Render unified view
    const renderUnifiedView = () => {
        const content = (
            <View>
            {hunks.map((hunk, hunkIndex) => (
                <View key={hunkIndex}>
                    {hunkIndex > 0 && (
                        <View style={{
                            backgroundColor: colors.hunkHeaderBg,
                            paddingVertical: 8,
                            paddingHorizontal: 16,
                            borderTopWidth: 1,
                            borderTopColor: colors.outline,
                            borderBottomWidth: 1,
                            borderBottomColor: colors.outline,
                        }}>
                            <Text style={{
                                color: colors.hunkHeaderText,
                                fontSize: 12,
                                fontFamily: Platform.select({
                                    ios: 'Menlo',
                                    android: 'monospace',
                                    default: 'monospace'
                                }),
                            }}>
                                @@ -{hunk.oldStart},{hunk.oldLines} +{hunk.newStart},{hunk.newLines} @@
                            </Text>
                        </View>
                    )}

                    {hunk.lines.map((line, lineIndex) => {
                        const isAdded = line.type === 'add';
                        const isRemoved = line.type === 'remove';

                        const lineStyle: ViewStyle = {
                            flexDirection: 'row',
                            backgroundColor: isAdded
                                ? colors.addedBg
                                : isRemoved
                                    ? colors.removedBg
                                    : colors.contextBg,
                            borderLeftWidth: 3,
                            borderLeftColor: isAdded
                                ? colors.addedBorder
                                : isRemoved
                                    ? colors.removedBorder
                                    : 'transparent',
                        };

                        const textColor = isAdded
                            ? colors.addedText
                            : isRemoved
                                ? colors.removedText
                                : colors.contextText;

                        return (
                            <View key={`${hunkIndex}-${lineIndex}`} style={lineStyle}>
                                {showLineNumbers && (
                                    <>
                                        <Text style={[lineNumberStyle, { transform: [{ scaleX: fontScaleX }] }]}>
                                            {line.oldLineNumber || ''}
                                        </Text>
                                        <Text style={[lineNumberStyle, { transform: [{ scaleX: fontScaleX }] }]}>
                                            {line.newLineNumber || ''}
                                        </Text>
                                    </>
                                )}

                                <Text style={{
                                    transform: [{ scaleX: fontScaleX }],
                                    width: 24,
                                    textAlign: 'center',
                                    color: textColor,
                                    fontFamily: Platform.select({
                                        ios: 'Menlo',
                                        android: 'monospace',
                                        default: 'monospace'
                                    }),
                                    fontSize: 13,
                                    backgroundColor: isAdded ? colors.addedBg : isRemoved ? colors.removedBg : colors.contextBg,
                                }}>
                                    {isAdded ? '+' : isRemoved ? '-' : ' '}
                                </Text>

                                {renderLineContent(line.content, [
                                    lineContentStyle, 
                                    { 
                                        color: textColor,
                                        paddingRight: 20
                                    },
                                    !wrapLines && { flexWrap: 'nowrap' }
                                ])}
                            </View>
                        );
                    })}
                </View>
            ))}
            </View>
        );

        return (
            <ScrollView 
                style={{ flex: 1, maxHeight }} 
                nestedScrollEnabled 
                showsVerticalScrollIndicator={true}
            >
                <ScrollView
                    ref={unifiedScrollRef}
                    horizontal={!wrapLines}
                    showsHorizontalScrollIndicator={!wrapLines}
                    contentContainerStyle={{ flexGrow: 1 }}
                >
                    {content}
                </ScrollView>
            </ScrollView>
        );
    };

    // Render split view
    const renderSplitView = () => {
        // Group lines for split view
        const splitLines: Array<{ old?: DiffLine; new?: DiffLine }> = [];
        let i = 0;

        while (i < lines.length) {
            const line = lines[i];

            if (line.type === 'normal') {
                splitLines.push({ old: line, new: line });
                i++;
            } else if (line.type === 'remove') {
                // Look ahead for adds
                const adds: DiffLine[] = [];
                let j = i + 1;
                while (j < lines.length && lines[j].type === 'add') {
                    adds.push(lines[j]);
                    j++;
                }

                // Pair removes with adds
                splitLines.push({ old: line, new: adds.shift() });
                adds.forEach(add => splitLines.push({ new: add }));
                i = j;
            } else if (line.type === 'add') {
                splitLines.push({ new: line });
                i++;
            }
        }

        return (
            <ScrollView 
                style={{ flex: 1, maxHeight }} 
                nestedScrollEnabled 
                showsVerticalScrollIndicator={true}
            >
                <ScrollView
                    ref={splitScrollRef}
                    horizontal={!wrapLines}
                    showsHorizontalScrollIndicator={!wrapLines}
                    contentContainerStyle={{ flexGrow: 1 }}
                >
                    <View style={{ flexDirection: 'row', flex: 1 }}>
                    {/* Old content column */}
                    <View style={{ flex: 1, borderRightWidth: 1, borderRightColor: colors.outline }}>
                        <View style={[headerStyle, { borderBottomWidth: 0 }]}>
                            <Text style={titleStyle}>{oldTitle}</Text>
                        </View>
                        {splitLines.map((pair, index) => {
                            const line = pair.old;
                            const isEmpty = !line;
                            const isRemoved = line?.type === 'remove';

                            const lineStyle: ViewStyle = {
                                flexDirection: 'row',
                                backgroundColor: isEmpty
                                    ? colors.contextBg
                                    : isRemoved
                                        ? colors.removedBg
                                        : colors.contextBg,
                                minHeight: 24,
                                borderLeftWidth: 3,
                                borderLeftColor: isRemoved && !isEmpty
                                    ? colors.removedBorder
                                    : 'transparent',
                            };

                            return (
                                <View key={`old-${index}`} style={lineStyle}>
                                    {showLineNumbers && (
                                        <Text style={[lineNumberStyle, { transform: [{ scaleX: fontScaleX }] }]}>
                                            {line?.oldLineNumber || ''}
                                        </Text>
                                    )}

                                    {line ? renderLineContent(line.content, [
                                        lineContentStyle,
                                        {
                                            color: isEmpty
                                                ? colors.contextText
                                                : isRemoved
                                                    ? colors.removedText
                                                    : colors.contextText,
                                            paddingLeft: 10,
                                            paddingRight: 20,
                                        },
                                        !wrapLines && { flexWrap: 'nowrap' }
                                    ]) : <Text style={lineContentStyle}></Text>}
                                </View>
                            );
                        })}
                    </View>

                    {/* New content column */}
                    <View style={{ flex: 1 }}>
                        <View style={[headerStyle, { borderBottomWidth: 0 }]}>
                            <Text style={titleStyle}>{newTitle}</Text>
                        </View>
                        {splitLines.map((pair, index) => {
                            const line = pair.new;
                            const isEmpty = !line;
                            const isAdded = line?.type === 'add';

                            const lineStyle: ViewStyle = {
                                flexDirection: 'row',
                                backgroundColor: isEmpty
                                    ? colors.contextBg
                                    : isAdded
                                        ? colors.addedBg
                                        : colors.contextBg,
                                minHeight: 24,
                                borderLeftWidth: 3,
                                borderLeftColor: isAdded && !isEmpty
                                    ? colors.addedBorder
                                    : 'transparent',
                            };

                            return (
                                <View key={`new-${index}`} style={lineStyle}>
                                    {showLineNumbers && (
                                        <Text style={[lineNumberStyle, { transform: [{ scaleX: fontScaleX }] }]}>
                                            {line?.newLineNumber || ''}
                                        </Text>
                                    )}

                                    {line ? renderLineContent(line.content, [
                                        lineContentStyle,
                                        {
                                            color: isEmpty
                                                ? colors.contextText
                                                : isAdded
                                                    ? colors.addedText
                                                    : colors.contextText,
                                            paddingLeft: 10,
                                            paddingRight: 20,
                                        },
                                        !wrapLines && { flexWrap: 'nowrap' }
                                    ]) : <Text style={lineContentStyle}></Text>}
                                </View>
                            );
                        })}
                    </View>
                    </View>
                </ScrollView>
            </ScrollView>
        );
    };

    return (
        <View style={containerStyle} className={className}>
            {/* Header */}
            <View style={headerStyle}>
                <Text style={titleStyle}>
                    {mode === 'split' ? 'Changes' : `${oldTitle} â†’ ${newTitle}`}
                </Text>

                {showDiffStats && (
                    <View style={{ flexDirection: 'row', gap: 8 }}>
                        <Text style={[statsStyle, { color: colors.success }]}>
                            +{stats.additions}
                        </Text>
                        <Text style={[statsStyle, { color: colors.error }]}>
                            -{stats.deletions}
                        </Text>
                    </View>
                )}
            </View>

            {/* Diff content */}
            {mode === 'split' ? renderSplitView() : renderUnifiedView()}
        </View>
    );
};

// Export a simple function to get diff stats without rendering
export const getDiffStats = (oldText: string, newText: string): { additions: number; deletions: number } => {
    const changes = Diff.diffLines(oldText, newText);
    let additions = 0;
    let deletions = 0;

    changes.forEach((change) => {
        const lineCount = change.value.split('\n').filter(line => line !== '').length;
        if (change.added) {
            additions += lineCount;
        } else if (change.removed) {
            deletions += lineCount;
        }
    });

    return { additions, deletions };
};

