            </Text>
            <Switch
              value={useCustom}
              onValueChange={setUseCustom}
              trackColor={{ false: colors.surfaceVariant, true: colors.primary }}
            />
          </View>
        </View>

        {/* Custom Input Fields */}
        {useCustom && (
          <View style={{ marginBottom: 16 }}>
            <View style={{ marginBottom: 12 }}>
              <Text style={{ 
                color: colors.onSurfaceVariant, 
                marginBottom: 4,
                fontSize: 14
              }}>
                Original Text:
              </Text>
              <TextInput
                value={customOld}
                onChangeText={setCustomOld}
                multiline
                numberOfLines={6}
                style={{
                  backgroundColor: colors.surfaceVariant,
                  padding: 12,
                  color: colors.onSurface,
                  fontFamily: 'monospace',
                  fontSize: 13,
                  minHeight: 120,
                  textAlignVertical: 'top',
                  borderWidth: 1,
                  borderColor: colors.outline
                }}
                placeholder="Enter original text..."
                placeholderTextColor={colors.onSurfaceVariant}
              />
            </View>

            <View>
              <Text style={{ 
                color: colors.onSurfaceVariant, 
                marginBottom: 4,
                fontSize: 14
              }}>
                Modified Text:
              </Text>
              <TextInput
                value={customNew}
                onChangeText={setCustomNew}
                multiline
                numberOfLines={6}
                style={{
                  backgroundColor: colors.surfaceVariant,
                  padding: 12,
                  color: colors.onSurface,
                  fontFamily: 'monospace',
                  fontSize: 13,
                  minHeight: 120,
                  textAlignVertical: 'top',
                  borderWidth: 1,
                  borderColor: colors.outline
                }}
                placeholder="Enter modified text..."
                placeholderTextColor={colors.onSurfaceVariant}
              />
            </View>
          </View>
        )}

          {/* Info */}
          <View style={{ 
            backgroundColor: colors.infoContainer, 
            padding: 16, 
            borderWidth: 1,
            borderColor: colors.info,
            marginBottom: 16
          }}>
            <Text style={{ 
              color: colors.info,
              fontSize: 16,
              fontWeight: '600',
              marginBottom: 8
            }}>
              About DiffView
            </Text>
            <Text style={{ color: colors.onSurface, lineHeight: 20 }}>
              This component uses the 'diff' library (jsdiff) to calculate differences between two texts. 
              It supports multiple diff types (lines, words, characters) and can display changes in either 
              unified or split view mode.
            </Text>
          </View>
        </View>
      </ScrollView>

      {/* Diff View - Full Height */}
      <View style={{ flex: 1, borderTopWidth: 1, borderTopColor: colors.outline }}>
        <View style={{ 
          backgroundColor: colors.surface,
          paddingHorizontal: 16,
          paddingVertical: 12,
          borderBottomWidth: 1,
          borderBottomColor: colors.outline
        }}>
          <Text style={{ 
            fontSize: 18, 
            fontWeight: '600', 
            color: colors.onBackground
          }}>
            Result
          </Text>
        </View>

        <DiffView
          oldText={oldText}
          newText={newText}
          mode={mode}
          diffType={diffType}
          showLineNumbers={showLineNumbers}
          contextLines={contextLines}
          oldTitle="Before"
          newTitle="After"
          maxHeight={undefined}
          style={{ flex: 1 }}
          wrapLines={wrapLines}
          fontScaleX={fontScaleX}
        />
      </View>
    </View>
  );
}

